ruleSearchList:
class.search-result-list
<js>
//过滤搜索结果：删除糢糊搜索结果或只保留书名长度最短的图书。
var rules = {
  pattern: /[?&]keyword=([^&|]+)/,
  name: 'class.tit@tag.a.0@text',
  author: 'class.bookinfo@tag.a.0@text'
};
var opts = {
  nonfuzzy: true,
  less: false,
  charset: 'UTF-8'
};

function searchFilter(rules, opts, schList){
  //依赖baseUrl、result变量。
  //return Array
  //schList List<Object>(result.toArray()): 要过滤的列表
  //pattern RegExp: 获取URL内searchKey的正则表达式
  //name String|RegExp: 书名规则
  //author String|RegExp: 作者规则
  //charset String('UTF-8'): URL编码方式
  //nonfuzzy Boolean(true): 删除模糊搜索结果
  //less Boolean(false): 只保留书名长度最短的图书

  schList = schList === undefined ? result.toArray() : schList;
  opts.charset = opts.charset === undefined ? 'UTF-8' : opts.charset;
  opts.nonfuzzy = opts.nonfuzzy === undefined ? true : !!opts.nonfuzzy;
  opts.less = opts.less === undefined ? false : !!opts.less;

  function getStr(ele, index){
    var nrIsReg = (rules.name instanceof RegExp);
    var arIsReg = (rules.author instanceof RegExp); 
    if (!nrIsReg || !arIsReg) { java.setContent(ele); }
    return {
      name: nrIsReg ? (String(ele).match(rules.name)||['',''])[1] : String(java.getString(rules.name)),
     author: arIsReg ? (String(ele).match(rules.author)||['',''])[1] : String(java.getString(rules.author)),
      index: index
    };
  }

  try {
    var kw = (baseUrl.match(rules.pattern) || ['',''])[1];
    kw = Packages.java.net.URLDecoder.decode(kw, opts.charset);
    var books = schList.map((e,i) => getStr(e,i)).filter(v => !opts.nonfuzzy || v.name.includes(kw) || v.author.includes(kw));
    var list = [];
    var min = Math.min.apply(null, books.map(v => v.name.length));
    var length = Math.max(String(kw).length, min);
    books.forEach(v => {
      if (!opts.less || v.name.length === length){ list.push(schList[v.index]); }
    });
    return list;
  } catch(e) {
    return schList;
  }
}

searchFilter(rules, opts);
</js>

ruleChapterList:
+<js>
//二级目录，仅支持HTML
var rules = {
  volsName: 
'//*[@class*="volume-list"]//*[@class~="^(?:.*\\s+)?volume(?:\\s+.*)?$"]/text()',
  volumes: '@css:.volume-list .chapter-list',
  chapters: 'tag.li',
  chName: 'tag.a.0@text',
  chLink: 'tag.a.0@href',
  vip: 'class.vip'
};
var opts = {
  showVolName: false
};

function chapterLevel(rules, opts){
  //return Array
  //volsName: 卷标题规则（XPath）
  //volumes: 卷规则
  //volInfo(' '): 卷简介规则（volumes子规则）
  //chapters: 章节规则（volumes子规则）
  //chName: 章节标题规则（chapters子规则）
  //chLink: 章节url规则（chapters子规则）
  //vip(' '): 判定vip的规则（chapters子规则
  //showVolName(false): 是否在章节名称前显示卷名

  var check = (rule) => rule !== undefined && rule !== '' ? rule : '@xpath:/x[@x="" and @x!=""]';
  rules.volInfo = check(rules.volInfo);
  rules.vip = check(rules.vip);
  opts.showVolName = opts.showVolName === undefined ? true : !!opts.showVolName;

  var NL = '<br/>', NP = "<br/>\uFEFF<br/>";
  var list = [];
  var volsName = java.getStringList(rules.volsName).toArray();

  java.getElements(rules.volumes).toArray().forEach((vol,idx) => {
    java.setContent(vol);
    var volInfo = '简介：' + NL + java.getStringList(rules.volInfo).toArray().join(NL);
    var chs = java.getElements(rules.chapters).toArray().map(ch => {
      java.setContent(ch);
      var isVip = !java.getElements(rules.vip).isEmpty();
      var chName = java.getString(rules.chName);
      return {
        text: (isVip ? '💰' : '').padStart(4, "\u2000")+(opts.showVolName ? volsName[idx]+"\x20" : '')+chName,
        href: java.getString(rules.chLink),
        name: chName
      };
    });

    volInfo += NP + '目录(' + chs.length + ')：' + NL;
    volInfo += chs.slice(0,10).map(v => v.name).join(NL);
    volInfo += chs.length > 10 ? NL + '……' : '';
    volInfo = volInfo.replace(/(\s)\1+/g, '$1');
    list.push({
      text: '🔻[ '+volsName[idx]+' ]',
      href: 'https://translate.google.cn/#view=home&op=translate&sl=zh-CN&tl=en&text='+encodeURIComponent(volInfo)
    });
    list = list.concat(chs);
  });

  return list;
}

chapterLevel(rules, opts);
</js>

