ruleToc.chapterList:
@js:
//完整目录需要验证码
//正文http://m.fushuzhai.cc/novel/bid/cid.html
//目录的某种规律是cid依次加1
//目录有时不需要验证码
//目录时而正序时而负序

try{

let bid=baseUrl.match(/(\d+)/)[1];
let chapter_num=result.match(/共(\d+)章节/)[1];
let cata=java.getElements("class.lb_mulu chapterList@tag.li.!-1@a").toArray();

let cid_1=cata[0].toString().match(/\/(\d+)\.html/)[1],
cid_2=cata[chapter_num>1?1:0].toString().match(/\/(\d+)\.html/)[1];

if(cid_2-cid_1==1){
let list=[];
for(i=1;i<=chapter_num;i++,cid_1++){
list.push({text:"分卷阅读"+i,href:"/novel/"+bid+"/"+cid_1+".html"});}
result=list;}

else if(cata.length==chapter_num){
result=cata.sort().map(x=>({text:x.text(),href:x.attr("href")}));}

else{result=[{text:"暂不支持本书目录的完整解析",href:baseUrl}]}
}
catch(e){[{text:"网站已改版6.27"+e,href:baseUrl}]}

ruleContent.content:
@js:

content={
    childNode:[],
    childNodes:java.getElements(".content@p").toArray(),
    UpWz:function(m, i) {
        var k = Math.ceil((i + 1) % this.code);
        k = Math.ceil(m - k);
        return k
    },
    load:function(){
  this.code=result.match(/codeurl=.(\d+)/)[1];
        var e =String(java.base64Decode(java.getString('[name=client]@content',!1))).split(/[A-Z]+%/);

        for (var i = 0; i < e.length; i++) {
            var k = this.UpWz(e[i], i);
            this.childNode[k]=this.childNodes[i]
        }}
}


if((/codeurl/).test(result)){
content.load();
result=content.childNode.join("<br>");}

if(baseUrl.match(/novel\/\d+\.html/)){
result="暂不支持本书目录解析,请到浏览器阅读"}

result=result.replace(/^net::ERR_.*/g,baseUrl+"加载失败,请刷新")

