result
   .toArray()
   .slice(0, 3)
   .map(function (el){
    var url = String(el.select('a').eq(1).attr('href')).replace(
      /^\/([^\/])/,
      baseUrl.match(/(.+\/\/.+?\/)/)[1] + '$1'
    );
