ruleToc.nextTocUrl:
@js:
var n=(JSON.parse(result).data.chapter.chapterCount)/50+1;
var list=[];
baseUrl=baseUrl.replace(/1&order=asc&site=/,'');
for(var i=2;i<n;i++){
var url=baseUrl+i+'&order=asc&site=';
list.push(url);
}
list;

